(108, '공부했던 함수', 'other', '', 'fgetcsv(handle, length, delimiter);\r\n\r\nstr_repeat(str, len);\r\n\r\n$ext = end(explode(delimiter, string)); // end() 배열에 가장 마지막 것을 가져옴\r\n\r\nstr_pad(str, len, pad_str, STR_PAD_LEFT);\r\n\r\ncheckdate(month, day, year);\r\n\r\nextract(); 배열 key를 변수명으로 바꿔줌\r\nconpact(); //extract의 반대\r\n$name = &quot;조용구&quot;; $address = &quot;여수&quot;;\r\n$arr = compact(&quot;name&quot;,&quot;address&quot;);\r\nprint_r($arr); // Array([name]=&gt;&quot;조용구&quot;,[address]=&gt;&quot;여수&quot;);\r\n\r\nInjection 대비코드\r\n\r\n$row = fetch(&quot;select * from member where id=''$id''&quot;);\r\nif($row){\r\n    if($pwd = $row[''pwd'']){\r\n        Login 확인\r\n    }\r\n}\r\n\r\n이렇게 하면 pwd를 입력할 수 밖에 없다.\r\n\r\neach(arr); // 변수속에 key, value를 배열로 준다. ex) $arr = each(arr); $arr[''key''], $arr[''value''];\r\n\r\nlist(val1, val2,.....) = $arr //배여레 있는 value를 변수로 저장해줌\r\n\r\n$arr = array(&quot;b&quot;=&gt;1000, &quot;a&quot;=&gt;10000,&quot;c&quot;=&gt;1200);\r\n\r\nsort() =&gt; [0]=&gt;1000, [1]=&gt;1200, [2]=&gt;10000  &lt;=&gt;  rsort();\r\nasort() =&gt; [b]=&gt;1000, [c]=&gt;1200, [a]=&gt;10000   &lt;=&gt;  arsort();\r\nksort() =&gt; [a]=&gt;10000, [b]=&gt;1000, [c]=&gt;1200   &lt;=&gt;   krsort();\r\n\r\nshuffle(arr); // 배열을 섞음\r\n\r\nnatsort($arr); //자연순 알고리즘 정렬\r\narray_pop($arr); 마지막 값을 가져오고 배열에서 삭제 &lt;=&gt; array_shift(); //앞에서 가져옴\r\narray_push($arr,val1,val2, ...); //array속에 value를 끝에서 부터 &lt;=&gt; array_unshift(); //앞에 넣어줌\r\n\r\nstrtr(str, from str, to str);\r\n\r\nctype_digit(); //숫자체크\r\nctype_alpha(); //알파벳 문자 체크\r\nctype_alnum(); //영, 숫자 체크\r\n\r\nrange(start,end);// 증가하는 문자나 숫자를 배열로 넣고 싶을 때\r\n\r\nchunk_split(str, len, del); //str을 len만큼 del 로 끊어서 반환\r\n\r\narray_sum(arr); //배열에 있는 값을 더해준다.\r\narray_merge(arr1, arr2, ...); //배열을 합친다.\r\narray_flip($arr); //key와 value를 교체\r\n\r\nbase_convert(num, now 진법, 변환할 진법);\r\n\r\ndecbin 10 -&gt; 2   decoct 10 -&gt; 8  dechex 10 -&gt; 16\r\nbindec 2 -&gt; 10  octdec 8 -&gt; 10 hexdec 16 -&gt; 10\r\n\r\n8진수