efox&nbsp;*/\r\n}\r\n\r\ndiv\r\n{&nbsp;&nbsp;//transform:matrix(rotate,scale,traslate,skew);\r\n&nbsp;&nbsp;&nbsp;transform:matrix(0.866,0.5,-0.5,0.866,0,0);\r\n&nbsp;&nbsp;&nbsp;-ms-transform:matrix(0.866,0.5,-0.5,0.866,0,0);&nbsp;/*&nbsp;IE&nbsp;9&nbsp;*/\r\n&nbsp;&nbsp;&nbsp;-moz-transform:matrix(0.866,0.5,-0.5,0.866,0,0);&nbsp;/*&nbsp;Firefox&nbsp;*/\r\n&nbsp;&nbsp;&nbsp;-webkit-transform:matrix(0.866,0.5,-0.5,0.866,0,0);&nbsp;/*&nbsp;Safari&nbsp;and&nbsp;Chrome&nbsp;*/\r\n&nbsp;&nbsp;&nbsp;-o-transform:matrix(0.866,0.5,-0.5,0.866,0,0);&nbsp;/*&nbsp;Opera&nbsp;*/\r\n}', '2012-12-08 15:20:57', '0000-00-00 00:00:00', ''),
(121, '3D Transform', 'Jquery', '', 'div\r\n{\r\ntransform:&nbsp;rotateX(120deg);\r\n-webkit-transform:&nbsp;rotateX(120deg);&nbsp;/*&nbsp;Safari&nbsp;and&nbsp;Chrome&nbsp;*/\r\n-moz-transform:&nbsp;rotateX(120deg);&nbsp;/*&nbsp;Firefox&nbsp;*/\r\n}\r\n\r\ndiv\r\n{\r\ntransform:&nbsp;rotateY(130deg);\r\n-webkit-transform:&nbsp;rotateY(130deg);&nbsp;/*&nbsp;Safari&nbsp;and&nbsp;Chrome&nbsp;*/\r\n-moz-transform:&nbsp;rotateY(130deg);&nbsp;/*&nbsp;Firefox&nbsp;*/\r\n}\r\n\r\ntranslate3d(x,y,z);\r\ntranslateX(x)\r\ntranslateY(y)\r\ntranslateZ(z)\r\nscale3d(x,y,z)\r\nscaleX(x)\r\nscaleY(y)\r\nscaleZ(z)\r\nrotate3d(x,y,z,angle)\r\nrotateX(angle)\r\nrotateY(angle)\r\nrotateZ(angle)\r\n', '2012-12-08 15:24:10', '0000-00-00 00:00:00', ''),
(122, 'Transitions', 'Jquery', '', '항상써줘야됨\r\ndiv:hover\r\n{\r\n    width:400px;\r\n}\r\n\r\ndiv\r\n{\r\ntransition: width 2s;\r\n-moz-transition: width 2s; /* Firefox 4 */\r\n-webkit-transition: width 2s; /* Safari and Chrome */\r\n-o-transition: width 2s; /* Opera */\r\n}\r\n가로를 2배로\r\n\r\ndiv //마우스를 올리면 회전하면서 2배로\r\n{\r\ntransition: width 2s, height 2s, transform 2s;\r\n-moz-transition: width 2s, height 2s, -moz-transform 2s;\r\n-webkit-transition: width 2s, height 2s, -webkit-transform 2s;\r\n-o-transition: width 2s, height 2s,-o-transform 2s;\r\n}\r\n\r\ndiv //2초뒤어 가로 커짐\r\n{\r\ntransition: width 1s linear 2s;\r\n/* Firefox 4 */\r\n-moz-transition:width 1s linear 2s;\r\n/* Safari and Chrome */\r\n-webkit-transition:width 1s linear 2s;\r\n/* Opera */\r\n-o-transition:width 1s linear 2s;\r\n}', '2012-12-08 15:31:42', '2012-12-08 15:33:13', ''),
(123, 'Animation', 'CSS3', '', '@keyframes을&nbsp;사용하여&nbsp;애니매이션&nbsp;효과를&nbsp;줌\r\n\r\ndiv\r\n{\r\nwidth:100px;\r\nheight:100px;\r\nbackground:red;\r\nanimation:myfirst&nbsp;5s;\r\n-moz-animation:myfirst&nbsp;5s;&nbsp;/*&nbsp;Firefox&nbsp;*/\r\n-webkit-animation:myfirst&nbsp;5s;&nbsp;/*&nbsp;Safari&nbsp;and&nbsp;Chrome&nbsp;*/\r\n-o-animation:myfirst&nbsp;5s;&nbsp;/*&nbsp;Opera&nbsp;*/\r\n}\r\n\r\n@keyframes&nbsp;myfirst\r\n{\r\nfrom&nbsp;{background:red;}\r\nto&nbsp;{background:yellow;}\r\n}\r\n\r\n@-moz-keyframes&nbsp;myfirst&nbsp;/*&nbsp;Firefox&nbsp;*/\r\n{\r\nfrom&nbsp;{background:red;}\r\nto&nbsp;{background:yellow;}\r\n}\r\n\r\n@-webkit-keyframes&nbsp;myfirst&nbsp;/*&nbsp;Safari&nbsp;and&nbsp;Chrome&nbsp;*/\r\n{\r\nfrom&nbsp;{background:red;}\r\nto&nbsp;{background:yellow;}\r\n}\r\n\r\n@-o-keyframes&nbsp;myfirst&nbsp;/*&nbsp;Opera&nbsp;*/\r\n{\r\nfrom&nbsp;{background:red;}\r\nto&nbsp;{background:yellow;}\r\n}', '2012-12-08 15:39:14', '0000-00-00 00:00:00', ''),
(124, 'Multiple Columns', 'CSS3', '', 'div&nbsp;//글들을&nbsp;3개로&nbsp;쪼갠다.\r\n{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-moz-column-count:3;&nbsp;/*&nbsp;Firefox&nbsp;*/\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-webkit-column-count:3;&nbsp;/*&nbsp;Safari&nbsp;and&nbsp;Chrome&nbsp;*/\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;column-count:3;\r\n}\r\n\r\ndiv&nbsp;//문단&nbsp;사이를&nbsp;40px로\r\n{\r\n-moz-column-gap:40px;&nbsp;/*&nbsp;Firefox&nbsp;*/\r\n-webkit-column-gap:40px;&nbsp;/*&nbsp;Safari&nbsp;and&nbsp;Chrome&nbsp;*/\r\ncolumn-gap:40px;\r\n}\r\n\r\ndiv&nbsp;//문단&nbsp;규칙\r\n{\r\n-moz-column-rule:3px&nbsp;outset&nbsp;#ff00ff;&nbsp;/*&nbsp;Firefox&nbsp;*/\r\n-webkit-column-rule:3px&nbsp;outset&nbsp;#ff00ff;&nbsp;/*&nbsp;Safari&nbsp;and&nbsp;Chrome&nbsp;*/\r\ncolumn-rule:3px&nbsp;outset&nbsp;#ff00ff;\r\n}', '2012-12-08 15:41:37', '0000-00-00 00:00:00', ''),
(125, 'User Interface', 'CSS3', '', 'div\r\n{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;resize&nbsp;:&nbsp;both;&nbsp;//리사이즈&nbsp;할&nbsp;수&nbsp;있게&nbsp;해줌\r\n}\r\n', '2012-12-08 16:00:32', '0000-00-00 00:00:00', ''),
